import { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";
const NewExpense = (props) => {
  const [isEditing, setisEditing] = useState(false);
  const formDataHandler = (formEnteredData) => {
    const expenseDataFromForm = {
      ...formEnteredData,
      id: Math.random().toString(),
    };
    //console.log(expenseDataFromForm);
    props.AddExpenseData(expenseDataFromForm); //pass data from child to parent
    setisEditing(false);
  };
  const toggleHandler = () => {
    setisEditing(true);
  };
  const toggleHandlerCancel = () => {
    setisEditing(false);
  };

  return (
    <div className="new-expense">
      {!isEditing && <button onClick={toggleHandler}>NewExpense</button>}
      {isEditing && (
        <ExpenseForm
          onSaveData={formDataHandler}
          onCancel={toggleHandlerCancel}
        />
      )}
    </div>
  );
};
export default NewExpense;
