import "./ExpenseForm.css";
import { useState } from "react";
const ExpenseForm = () => {
  const [updateTitle, setUpdateTitle] = useState("");
  const [updateAmount, setUpdateAmount] = useState("");
  const [updateDate, setUpdateDate] = useState("");

  const updateTitleHandler = (event) => {
    setUpdateTitle(event.target.value);
  };
  const updateAmaountHandler = (event) => {
    setUpdateAmount(event.target.value);
  };
  const updateDateHandler = (event) => {
    setUpdateDate(event.target.value);
  };
  const submitHandler = (event) => {
    event.preventDefault();
    const ExpenseData = {
      updateTitle: event.target.value,
      updateAmount: event.target.value,
      updateDate: event.target.value,
    };
  };
  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={updateTitleHandler} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            step="0.01"
            min="0.01"
            onChange={updateAmaountHandler}
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={updateDateHandler}
          />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};
export default ExpenseForm;
