import "./Chart.css";
import ChartBars from "./ChartBars";
const Chart = (props) => {
  const ArrChartData = props.dataPoints.map((dtaPoints) => dtaPoints.value);
  const Maxvalue = Math.max(...ArrChartData); //Math.max only accept individual element so we spread out the numbers fro the array
  return (
    <div className="chart">
      {props.dataPoints.map((dataPoint) => (
        <ChartBars
          value={dataPoint.value}
          maxValue={Maxvalue}
          label={dataPoint.label}
        />
      ))}
    </div>
  );
};
export default Chart;
